import styled from "styled-components";

export const HeaderContainer = styled.div`
    background-color: #5DADE2;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 10rem;
`

const styles = theme => ({
    textField: {
        width: '90%',
        marginLeft: 'auto',
        marginRight: 'auto',            
        paddingBottom: 0,
        marginTop: 0,
        fontWeight: 500
    },
    input: {
        color: 'white'
    }
});
