import styled from "styled-components";

export const TableContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-content: stretch;
    align-items: flex-start;
    margin: 0;

    th {
        border: 1px solid black;
        width: 8rem;
        height: 2rem;
        display:flex;
        padding:8px;
    }

    tr{
        border: 1px solid black;
        display:flex;
    }

    td{
        border: 1px solid black;
        width: 8rem;
        height: 2rem;
        padding:8px;
    }
`
