import styled from "styled-components";

export const TableContainer = styled.div`
    table {
    font-family: arial, sans-serif;
    border-collapse: collapse;
    width: 100%;
    }

    td, th {
    border: 1px solid black;
    text-align: left;
    padding: 8px;
    }

    tr:nth-child(1) {
    background-color: white;
    }

    tr:nth-child(2) {
    background-color: white;
    }

    tr:nth-child(3){
    background-color: white;
    }
`
