import styled from "styled-components";

export const TableContainer = styled.div`
   tr{
        background-color: white;
        th{
            height: 40px;
        }
        td:nth-child(1){
            width: 40px;
            height: 40px;
            text-align: center;
        }
        td:nth-child(2){
            width: 10rem;
            padding: 0 0.5em;
        }
        td:nth-child(3){
            width: 10rem;
            padding: 0 0.5em;
        }
        td:nth-child(4){
            width: 7rem;
            padding: 0 0.5em;
        }
    }

`

export const UsersContainer = styled.div`
    font-family: arial,sans-serif;
    border-collapse: collapse;
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: stretch;
`
