import styled from "styled-components";

export const TableContainer = styled.div`
    #collapseTable { 
        border-collapse: collapse; 
    } 

   tr{
        background-color: white;
        th{
            height: 40px;
            border: 1px solid black;
            width: 10rem;
        }
        td:nth-child(1){
            width: 2rem;
            padding: 0 0.5em;
            height: 2rem;
            text-align: center;
            border: 1px solid black;
            border-collapse: collapse;
        }
        td:nth-child(2){
            width: 10rem;
            padding: 0 0.5em;
            border: 1px solid black;
        }
        td:nth-child(3){
            width: 10rem;
            padding: 0 0.5em;
            border: 1px solid black;
        }
        td:nth-child(4){
            width: 7rem;
            padding: 0 0.5em;
            border: 1px solid black;
            margin-right: auto;
            margin-left: auto;
        }
    }

`
